import { useEffect, useState } from "react";
import axios from "axios";
import './App.css';

function App() {
  const [message, setMessage] = useState("");

  useEffect(() => {
    axios.get("http://localhost:5000/") // backend Express URL
      .then(res => {
        setMessage(res.data);
      })
      .catch(err => {
        console.error("API çağrısı başarısız:", err);
      });
  }, []);

  return (
    <div className="container mt-5">
      <h1 className="text-center">Express'ten Gelen Mesaj</h1>
      <div className="alert alert-dan text-center" role="alert">
        {message}
      </div>
    </div>
  );
}

export default App;
