// client/src/pages/Login.js (örnek, form submit handler)
import React, { useContext } from "react";
import { AuthContext } from "../context/AuthContext";
import { useNavigate } from "react-router-dom";

function Login() {
    const { login } = useContext(AuthContext);
    const navigate = useNavigate();

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch("http://localhost:5000/api/auth/login", {
                method: "POST",
                headers: { "Content-Type": "application/json" },
                body: JSON.stringify({ email, password }),
            });

            const data = await response.json();

            if (!response.ok) {
                console.error("Giriş başarısız:", data.message || data.error || "Hata");
                alert(data.message || data.error || "Giriş yapılamadı.");
                return;
            }

            login(data.user); // AuthContext login
            navigate("/profile");
        } catch (err) {
            console.error("İstek sırasında hata:", err);
            alert("Sunucuya bağlanırken bir hata oluştu.");
        }
    };


    return (
        <div className="container d-flex justify-content-center align-items-center" style={{ minHeight: "100vh" }}>
            <div className="card p-4" style={{ width: "400px" }}>
                <h3 className="text-center mb-4">Login</h3>
                <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                        <label htmlFor="username" className="form-label">Username or E-mail:</label>
                        <input type="text" name="username" id="username" className="form-control" placeholder="Enter your username or email" />
                    </div>
                    <div className="mb-3">
                        <label htmlFor="password" className="form-label">Password:</label>
                        <input type="password" name="password" id="password" className="form-control" placeholder="Enter your password" />
                    </div>
                    <div className="d-flex justify-content-between">
                        <button type="submit" className="btn btn-primary">Login</button>
                        <p className="mb-0">
                            Don't have an account? <a href="/signup">Signup</a>
                        </p>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default Login;
